function notificationCenter(options){if(typeof options=="undefined")options=new Object();var element=document.createElement("ul"),autoStyle=true,elClass="notification-center";if(typeof options.autoStyle!="undefined")autoStyle=options.autoStyle;if(autoStyle){element.style.position="fixed";element.style.bottom="15px";element.style.right="15px";element.style.zIndex="1000";element.style.color="black";}
if(typeof options.class!="undefined")elClass=options.class;element.setAttribute("class",elClass);document.body.appendChild(element);this.notify=function(options){var n=new notification(options);if(!this.element().firstChild){this.element().appendChild(n.element());}else{this.element().insertBefore(n.element(),this.element().firstChild);}
if(options.fadein==true){n.element().style.opacity=0;function fadeIn(el){if(el.style.opacity>=1)return;el.style.opacity=parseFloat(el.style.opacity)+0.02;var timeout=window.setTimeout(fadeIn,10,el);}
fadeIn(n.element());}
return n;}
this.element=function(){return element;}
this.destroy=function(){}}
function notification(options){if(typeof options=="undefined")options=new Object();var element=document.createElement("li"),title=document.createElement("div"),content=document.createElement("div"),image=document.createElement("img"),closeButton=document.createElement("span"),elClass="notification";if(typeof options.class!="undefined")elClass=options.class;element.setAttribute("class",elClass);content.setAttribute("class","notification-content");var autoStyle=true;if(typeof options.autoStyle!="undefined")autoStyle=options.autoStyle;if(autoStyle){element.style.listStyle="none";element.style.fontSize="14px";element.style.background="#fafafa";element.style.padding="5px";element.style.margin="5px 0";element.style.border="2px solid #ccc";element.style.borderRadius="5px";element.style.height="70px";element.style.width="250px";element.style.position="relative";}
if(typeof options.title!="undefined"){var title=document.createElement("div");title.setAttribute("class","notification-title");title.textContent=options.title;title.style.fontWeight="bold";element.appendChild(title);}
if(typeof options.html=="undefined"){content.textContent=options.text;}else{content.innerHTML=options.html;}
element.appendChild(content);this.show=function(){element.style.display="block";return null;}
this.hide=function(){element.style.display="none";return null;}
destroy=function(element){element.parentNode.removeChild(element);return;}
this.destroy=function(){destroy(this.element());}
this.title=function(text){if(typeof text=="undefined"){return title.textContent;}
title.textContent=text;return true;}
this.text=function(text){if(typeof text=="undefined"){return content.textContent;}
content.textContent=text;return true;}
this.html=function(html){if(typeof html=="undefined"){return content.innerHTML;}
content.innerHTML=html;return true;}
this.element=function(){return element;}
closeButton.setAttribute("class","notification-close-button");closeButton.innerHTML="&times;";if(autoStyle){closeButton.style.color="#aaa";closeButton.style.position="absolute";closeButton.style.top="2px";closeButton.style.right="8px";closeButton.style.zIndex="2000";closeButton.style.cursor="pointer";closeButton.style.fontSize="18px";closeButton.style.zIndex="2000";closeButton.onmouseover=function(){this.style.color="#888";}
closeButton.onmouseout=function(){this.style.color="#aaa";}}
closeButton.onmousedown=function(){element.parentNode.removeChild(element);}
element.appendChild(closeButton);function fadeOut(el){if(el.style.opacity<=0){destroy(el);return;}
el.style.opacity=parseFloat(el.style.opacity)-0.1;if(el.style.opacity<0.2){el.style.opacity=0;}
var timeout=window.setTimeout(fadeOut,10,el);}
if(typeof options.autoClose!="undefined"){var time=parseInt(options.autoClose);if(time>0){window.setTimeout(function(element){element.style.opacity=1;fadeOut(element);},time,element);}}
return this;}